export default class Second extends Component {
    constructor()
    {
        super();
        this.state = {
            data:[],
            isVisible:null,
            string:""
        }
        this.getdata = this.getdata.bind(this);

    }
    componentDidMount()
    {
        this.setState({data:this.props.posts});
    }
    getdata(val)
    {
        console.log("data")
        this.setState({string:val.target.value});
    }
    addpost()
    {
        console.log("datajjjjj")
        const count = this.state.data.length;
        let post = {};
        post.title = this.state.string;
        post.id = count + 1;
        let a = this.state.data;
         a.push(post);
        this.setState({data:a});
    }

    deleteData(val)
    {
        const obj = this.state.data.filter((value)=> value.id != val);

        this.setState({data:obj}); 
    }
    Update(val)
    {
      var object = this.state.data.find((value)=> val==value.id);
      this.setState({isVisible:val});
    }
  render() {
    return (
      <div>
          <input type="text" onChange={this.getdata} />
          &nbsp;
          <button onClick={this.addpost.bind(this)}>add posts</button>

          {this.state.isVisible? this.state.data.map((val,index)=>{
                  return (
                      <div key={index}>
                         <h2> {val.id }</h2>
                         <h2> {val.title} </h2>
                         <button onClick={()=>this.deleteData(val.id)}>delete</button>
                         <button onClick={()=>this.Update(val.id)}>Update</button>
                    </div>
                  )
