const initialState ={
    posts: [],
}
const Post_reducer = (state = initialState ,action) =>{

    switch(action.type){ 
        case 'GET_POSTS':
            return {
                ...state
            }
        case 'ADD_POSTS':
            return {
            ...state,
            posts : state.posts.concat(action.payload)
        }
        case 'UPDATE_POSTS':
            return {
                ...state
            }

        case 'DELETE_POST':
            return {
                ...state,
            }
        default:
            return state
    }
}
export default Post_reducer;
