import React, { Component } from 'react'

export default class Second extends Component {
    constructor()
    {
        super();
        this.state = {
            data:[],
            isVisible:"true",
            string:""
        }
        this.getdata = this.getdata.bind(this);
    }
    componentDidMount()
    {
        this.setState({data:this.props.posts});
    }
    getdata(val)
    {
        console.log("data")
        this.setState({string:val.target.value});
    }
    addpost()
    {
        let count = this.state.data.length;
        let post = {};
        post.title = this.state.string;
        post.id = count + 1;
        let a = this.state.data;
        a.push(post);
        this.setState({data:a});
    }
  render() {
    return (
      <div>
          <input type="text" onChange={this.getData} />
          &nbsp;
          <button onClick={this.addpost}>add posts</button>
          {
              this.state.data.map((val,index)=>{
                  return (
                      <div key={index}>
                         <h2> {val.id }</h2>
                         <h2> {val.title} </h2>
                    </div>
                  )
              })
          }

      </div>
    )
  }
}
