const initialState ={
    post: [],
}
const PostReducer = (state = initialState,action) =>{
    console.log(676,action.payload)

    switch(action.type)
    {   
        case 'GET_POSTS':
            return {
                ...state
            }
        case 'ADD_POSTS':
        return {
            ...state,
            post : state.post.concat(action.payload)
        }
        case 'UPDATE_POSTS':
            return {
                ...state
            }

        case 'DELETE_POST':
            return {
                ...state,
            }
    }
}
export default PostReducer;
