const initialState ={
    post: [{id:1, title:"post1"}, {id:2, title:"post2"}],
}
const PostReducer = (state = initialState,action) =>{

    switch(action.type)
    {   
        case 'GET_POSTS':
            return {
                ...state
            }
        case 'ADD_POSTS':
        return {
            ...state
        }
        case 'UPDATE_POSTS':
            return {
                ...state
            }

        case 'DELETE_POST':
            return {
                ...state,
            }
    }
}
export default PostReducer;
