import React from "react";

export default class Hello extends React.Component {
    constructor() {
        super();
        this.state ={
            show:"",
            check:false,
         }
         this.GetData=this.GetData.bind(this);
         this.showText = this.showText.bind(this);
    }
    GetData(val)
    {
        console.log(val.target.value);
      this.setState({show:val.target.value});
    }

    showText()
     {
         console.log("run helloow");
        this.setState({check:true});
      }

    render() {
        return (
            <div id="box1">
                <br />
                <h1>hi, pushparaj singh</h1><br />
                <input type="text" placeholder="enter the text" onChange={this.GetData}/>&nbsp;&nbsp;
                <button onClick={()=>this.showText()}>show text</button>
                <h1>{ this.state.check?this.state.show:"" }</h1>
                <br/><br/>
            </div >
        );
    };
}
