import React, { Component } from 'react'

export default class Posttwo extends Component {
    constructor()
    {
        super();
        this.state = {
            data:[],
            inputvalue:""
        }
        this.addvalue = this.addvalue.bind(this);
    }

    componentDidMount()
    {
        this.setState({data:this.props.posts});
    }
    addvalue(val)
    {
        this.setState({inputvalue:val.target.value});
    }
  render() {
    return (
      <div>
          <input type={"text"} onChange={this.addvalue}/>
          <button onClick={this.addPost}>Add Post</button>

          {
            this.state.data.map((value,index)=>{
           return( <div key={index}>
           <h2>{value.id}</h2>
           <h2>{value.title}</h2>
            </div>)
            })

          }
      </div>
    )
  }
}
